client_script:
frappe.web_form.on('country', (field, value) => {
	set_reqd_fields(value);
	if (value == "Peru") {
		frappe.call({
			method: "frappe.contacts.doctype.address.address.get_departments",
			args: {
				country: value
			},
			callback: function(r, rt){
				if(r.message){
					frappe.web_form.set_df_property('departamento', 'options', r.message.reverse());
					frappe.web_form.set_df_property('provincia', 'options', []);
					frappe.web_form.set_df_property('distrito', 'options', []);
					frappe.web_form.set_value("ubigeo", "");
				}
			}
		});
	}
});
frappe.web_form.on('departamento', (field, value) => {
	var country = frappe.web_form.get_value(['country']);
	if (country == "Peru") {
		frappe.call({
			method: "frappe.contacts.doctype.address.address.get_provinces",
			args: {
				country: country,
				department: value
			},
			callback: function(r, rt){
				if(r.message){
					frappe.web_form.set_df_property('provincia', 'options', r.message.reverse());
					frappe.web_form.set_df_property('distrito', 'options', []);
					frappe.web_form.set_value("ubigeo", "");
					frappe.web_form.events.trigger('provincia')
				}
			}
		});
	}
});
frappe.web_form.on('provincia', (field, value) => {
	var country = frappe.web_form.get_value(['country']);
	var department = frappe.web_form.get_value(['departamento']) || frappe.web_form.doc.departamento;
	if (country == "Peru" && department != null) {
		frappe.call({
			method: "frappe.contacts.doctype.address.address.get_districts",
			args: {
				country: country,
				department: department,
				province: value
			},
			callback: function(r, rt){
				if(r.message){
					frappe.web_form.set_df_property('distrito', 'options', r.message.reverse());
					frappe.web_form.set_value("ubigeo", "");
				}
			}
		});
	}
});
frappe.web_form.on('distrito', (field, value) => {
	var country = frappe.web_form.get_value(['country']);
	var department = frappe.web_form.get_value(['departamento']) || frappe.web_form.doc.departamento;
	var province = frappe.web_form.get_value(['provincia']) || frappe.web_form.doc.provincia;
	if (country == "Peru" && department != null  && province != null) {
		frappe.call({
			method: "frappe.contacts.doctype.address.address.get_ubigeo",
			args: {
				country: country,
				department: department,
				province: province,
				district: value
			},
			callback: function(r, rt){
				if(r.message){
					frappe.web_form.set_value("ubigeo", r.message);
				}
			}
		});
	}
});
function set_reqd_fields(country){
	frappe.web_form.set_df_property('city', 'reqd', (country=="Peru") ? 0 : 1);
	frappe.web_form.set_df_property('state', 'reqd', (country=="Peru") ? 0 : 1);
	frappe.web_form.set_df_property('departamento', 'reqd', (country=="Peru") ? 1 : 0);
	frappe.web_form.set_df_property('provincia', 'reqd', (country=="Peru") ? 1 : 0);
	frappe.web_form.set_df_property('distrito', 'reqd', (country=="Peru") ? 1 : 0);
	frappe.web_form.set_df_property('ubigeo', 'reqd', (country=="Peru") ? 1 : 0);
};
